def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context) def projectDir = groovyUtils.projectPath File file = new File(projectDir) def firstLevelDir = file.getParent() def filePath = firstLevelDir +"\\MySOAPProject\\Test data for Verify info about country.txt" context.fileReader = new BufferedReader(new FileReader(filePath)) rows = context.fileReader.readLines() def n = rows.size def res def responseHolder for (int i =1; i< n; i++) { row=rows[i] String[] data = row.split(',') testRunner.testCase.setPropertyValue("countryCode", data[0]) testRunner.testCase.setPropertyValue("countryName", data[1]) testRunner.testCase.setPropertyValue("capital", data[2]) testRunner.testCase.setPropertyValue("phone", data[3]) testRunner.testCase.setPropertyValue("continent", data[4]) testRunner.testCase.setPropertyValue("currency", data[5]) testRunner.testCase.setPropertyValue("langCode", data[6]) testRunner.testCase.setPropertyValue("lang", data[7])
testRunner.runTestStepByName("FullCountryInfo - Request 1") res = context.expand('${FullCountryInfo - Request 1#Response}') responseHolder = new XmlSlurper().parseText(res)
assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sISOCode == data[0] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sName == data[1] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sCapitalCity == data[2] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sPhoneCode == data[3] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sContinentCode == data[4] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.sCurrencyISOCode == data[5] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.Languages.tLanguage.sISOCode == data[6] assert responseHolder.Body.FullCountryInfoResponse.FullCountryInfoResult.Languages.tLanguage.sName == data[7] }
